angular.module('bethSite')
.controller('loginCtrl', ["$scope", "$location", "authService", function($scope, $location, authService) {

  $scope.isUser = authService.isLoggedIn();

  $scope.login = function () {
    //initial values
    $scope.error = false;
    $scope.disabled = true;
    //call login from service
    authService.login($scope.loginForm.username, $scope.loginForm.password)
    //handle success
    .then (function () {
      $location.path('/');
      $scope.disabled = false;
      $scope.loginForm = {};
    })
    //handle error
    .catch(function() {
      $scope.error = true;
      $scope.errorMessage = "Invalid username and/or password";
      $scope.disabled = false;
      $scope.loginForm = {};
    })
  }
}])

.controller('logoutCtrl', ["$scope", "$location", "authService", function($scope, $location, authService) {

  $scope.logout = function () {
    //call logout from service
    authService.logout()
      .then(function() {
        $location.path('/login');
      })
  }
}])

.controller('signupCtrl', ["$scope", "$location", "authService", function($scope, $location, authService) {

  $scope.register = function() {
    //initial values
    $scope.error = false;
    $scope.disabled = true;

    //call signup from service
    authService.register(
      $scope.registerForm.username,
      $scope.registerForm.password,
      $scope.registerForm.firstName,
      $scope.registerForm.lastName,
      $scope.registerForm.email,
      $scope.registerForm.number,
      $scope.registerForm.street,
      $scope.registerForm.city,
      $scope.registerForm.state,
      $scope.registerForm.zip
    )
    //handle success
    .then(function () {
      $location.path('/login');
      $scope.disabled = false;
      $scope.registerForm = {};
    })
    //handle error
    .then(function () {
      $scope.error = true;
      $scope.errorMessage = "Something went wrong!";
      $scope.disabled = false;
      $scope.registerForm = {};
    })
  }
}])



